import React from "react";
import '../css/estilos.css';

function Visualizar(props) {

  const verDatos = () => {
    // Patient
    let name = localStorage.getItem("name");
    let document = localStorage.getItem("document");
    let gender = localStorage.getItem("gender");
    let birthDate = localStorage.getItem("birthDate");
    let age = localStorage.getItem("age");
    let email = localStorage.getItem("email");
    let phone = localStorage.getItem("phone");
    let address = localStorage.getItem("address");

    // Condition
    let titlec = localStorage.getItem("titlec");
    let code = localStorage.getItem("code");
    let clinicalStatus = localStorage.getItem("clinicalStatus");
    let VerificationStatus = localStorage.getItem("VerificationStatus");

    // Categoria
    let category = localStorage.getItem("category");
    let reason = localStorage.getItem("reason");
    let status = localStorage.getItem("status");
    let period = localStorage.getItem("period");

    // let person = localStorage.getItem("person")
    // let person = JSON.parse(localStorage.getItem("person"));

    // Patient
    let person = { name, document, gender, birthDate, age, email, phone, address };
    saveData(person);

    let condition = {titlec, code, clinicalStatus, VerificationStatus}

  }

  const saveData = (data) => {
    var url = 'http://localhost:8000/api/pacientes';
    // var url = 'mongodb+srv://admin:admin@cluster0.lsxg7.mongodb.net/pacientesbasedatos?retryWrites=true&w=majority';

    fetch(url, {
      method: 'POST', // or 'PUT'
      body: JSON.stringify(data), // data can be `string` or {object}!
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => res.json())
      .catch(error => console.error('Error:', error))
      .then(response => console.log('Success:', response));
  }

  
  return (
    <div>
      <div className="divHC">
        <h1 onClick={verDatos}> Historia Cl√≠nica</h1>
      </div>
      <div className="viewerIframe">
        <iframe className="ifr"
          // src="../public/archivos/Document.html"
          src={process.env.PUBLIC_URL + 'archivos/Document.html'}
        // src={process.env.PUBLIC_URL + cargarArchivo}
        />
      </div>
    </div>
  )
}

export default Visualizar;
